import { useEffect, useState } from "react";
import { supabase } from "./supabase";

function App() {
  const [tab, setTab] = useState("inventario");
  const [materiales, setMateriales] = useState([]);
  const [historial, setHistorial] = useState([]);
  const [nuevoMaterial, setNuevoMaterial] = useState(false);
  const [formIngreso, setFormIngreso] = useState({ nombre: "", cantidad: "", contenedor: "", descripcion: "" });
  const [formSalida, setFormSalida] = useState({ nombre: "", cantidad: "", responsable: "", observacion: "" });

  const handleChangeIngreso = (e) => setFormIngreso({ ...formIngreso, [e.target.name]: e.target.value });
  const handleChangeSalida = (e) => setFormSalida({ ...formSalida, [e.target.name]: e.target.value });

  const obtenerInventario = async () => {
    const { data, error } = await supabase.from("inventario").select("*");
    if (!error) setMateriales(data);
  };

  const handleSubmitIngreso = async (e) => {
    e.preventDefault();
    const { nombre, cantidad, contenedor, descripcion } = formIngreso;
    if (!nombre || !cantidad) return alert("Por favor, completa los campos obligatorios");

    const cantidadNum = parseInt(cantidad);

    const { data: existente, error: fetchError } = await supabase
      .from("inventario")
      .select("*")
      .eq("nombre", nombre)
      .single();

    if (fetchError && fetchError.code !== "PGRST116") return alert("Error al verificar existencia");

    if (existente) {
      const nuevaCantidad = existente.cantidad + cantidadNum;
      const { error: updateError } = await supabase
        .from("inventario")
        .update({ cantidad: nuevaCantidad })
        .eq("id", existente.id);

      if (updateError) return alert("Error al actualizar cantidad");
    } else {
      const { error: insertError } = await supabase
        .from("inventario")
        .insert([{ nombre, cantidad: cantidadNum, contenedor, descripcion }]);

      if (insertError) return alert("Error al registrar nuevo material");
    }

    setHistorial([
      ...historial,
      {
        tipo: "Ingreso",
        nombre,
        cantidad: cantidadNum,
        contenedor,
        descripcion,
        fecha: new Date().toLocaleString(),
        responsable: "Sistema",
      },
    ]);

    setFormIngreso({ nombre: "", cantidad: "", contenedor: "", descripcion: "" });
    setNuevoMaterial(false);
    obtenerInventario();
  };

  const handleSubmitSalida = async (e) => {
    e.preventDefault();
    const { nombre, cantidad, responsable, observacion } = formSalida;
    if (!nombre || !cantidad || !responsable) return alert("Por favor, completa todos los campos");

    const cantidadNum = parseInt(cantidad);
    const { data: material, error: fetchError } = await supabase
      .from("inventario")
      .select("*")
      .eq("nombre", nombre)
      .single();

    if (fetchError || !material) return alert("Material no encontrado");
    if (material.cantidad < cantidadNum) return alert("No hay suficiente cantidad en inventario");

    const nuevaCantidad = material.cantidad - cantidadNum;

    const { error: updateError } = await supabase
      .from("inventario")
      .update({ cantidad: nuevaCantidad })
      .eq("id", material.id);

    if (updateError) return alert("Error al actualizar inventario");

    const { error: insertError } = await supabase.from("historial").insert([
      {
        tipo: "Salida",
        nombre,
        cantidad: cantidadNum,
        responsable,
        observacion,
        fecha: new Date().toISOString(),
      },
    ]);

    if (insertError) return alert("Error al guardar en historial");

    setHistorial([
      ...historial,
      {
        tipo: "Salida",
        nombre,
        cantidad: cantidadNum,
        responsable,
        observacion,
        fecha: new Date().toLocaleString(),
      },
    ]);

    setFormSalida({ nombre: "", cantidad: "", responsable: "", observacion: "" });
    obtenerInventario();
  };

  useEffect(() => {
    obtenerInventario();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-5xl mx-auto bg-white shadow-lg rounded-2xl p-6">
        <h1 className="text-3xl font-bold text-center mb-6">📦 Sistema de Inventario de Bodegas</h1>

        <div className="flex justify-center gap-3 mb-6 flex-wrap">
          {["inventario", "ingreso", "salida", "historial"].map((tabName) => (
            <button key={tabName} onClick={() => setTab(tabName)} className={`px-4 py-2 rounded-lg ${tab === tabName ? "bg-blue-600 text-white" : "bg-gray-200"}`}>
              {tabName.charAt(0).toUpperCase() + tabName.slice(1)}
            </button>
          ))}
        </div>

        <div className="bg-gray-50 p-4 rounded-xl">
          {tab === "inventario" && (
            <div>
              <h2 className="text-xl font-semibold mb-4">📋 Tabla de Inventario</h2>
              {materiales.length === 0 ? (
                <p className="text-gray-500 italic">Aún no hay materiales ingresados.</p>
              ) : (
                <table className="w-full text-sm text-left border border-gray-300">
                  <thead className="bg-gray-200">
                    <tr>
                      <th className="px-4 py-2">Nombre</th>
                      <th className="px-4 py-2">Cantidad</th>
                      <th className="px-4 py-2">Contenedor</th>
                      <th className="px-4 py-2">Descripción</th>
                    </tr>
                  </thead>
                  <tbody>
                    {materiales.map((item, i) => (
                      <tr key={i} className="border-t">
                        <td className="px-4 py-2">{item.nombre}</td>
                        <td className="px-4 py-2">{item.cantidad}</td>
                        <td className="px-4 py-2">{item.contenedor}</td>
                        <td className="px-4 py-2">{item.descripcion}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          )}

          {tab === "ingreso" && (
            <form className="grid gap-3" onSubmit={handleSubmitIngreso}>
              <div className="flex items-center gap-2">
                <label className="font-semibold">Material:</label>
                <select
                  className="p-2 border rounded flex-1"
                  value={formIngreso.nombre}
                  name="nombre"
                  onChange={(e) => {
                    handleChangeIngreso(e);
                    setNuevoMaterial(e.target.value === "nuevo");
                  }}
                >
                  <option value="">-- Selecciona o agrega --</option>
                  {materiales.map((item) => (
                    <option key={item.id} value={item.nombre}>{item.nombre}</option>
                  ))}
                  <option value="nuevo">➕ Agregar nuevo material</option>
                </select>
              </div>

              {nuevoMaterial && (
                <>
                  <input className="p-2 border rounded" placeholder="Nombre del nuevo material" name="nombre" value={formIngreso.nombre === "nuevo" ? "" : formIngreso.nombre} onChange={handleChangeIngreso} />
                  <input className="p-2 border rounded" placeholder="Contenedor" name="contenedor" value={formIngreso.contenedor} onChange={handleChangeIngreso} />
                  <input className="p-2 border rounded" placeholder="Descripción" name="descripcion" value={formIngreso.descripcion} onChange={handleChangeIngreso} />
                </>
              )}

              <input className="p-2 border rounded" placeholder="Cantidad a ingresar" name="cantidad" value={formIngreso.cantidad} onChange={handleChangeIngreso} />
              <button type="submit" className="bg-blue-600 text-white py-2 rounded hover:bg-blue-700">Registrar ingreso</button>
            </form>
          )}

          {tab === "salida" && (
            <form className="grid gap-3" onSubmit={handleSubmitSalida}>
              <div className="flex items-center gap-2">
                <label className="font-semibold">Material:</label>
                <select
                  className="p-2 border rounded flex-1"
                  value={formSalida.nombre}
                  name="nombre"
                  onChange={handleChangeSalida}
                >
                  <option value="">-- Selecciona material --</option>
                  {materiales.map((item) => (
                    <option key={item.id} value={item.nombre}>{item.nombre}</option>
                  ))}
                </select>
              </div>
              <input className="p-2 border rounded" placeholder="Cantidad a sacar" name="cantidad" value={formSalida.cantidad} onChange={handleChangeSalida} />
              <input className="p-2 border rounded" placeholder="Responsable" name="responsable" value={formSalida.responsable} onChange={handleChangeSalida} />
              <input className="p-2 border rounded" placeholder="Observación" name="observacion" value={formSalida.observacion} onChange={handleChangeSalida} />
              <button type="submit" className="bg-red-600 text-white py-2 rounded hover:bg-red-700">Registrar salida</button>
            </form>
          )}

          {tab === "historial" && (
            <div>
              <h2 className="text-xl font-semibold mb-4">🕓 Historial de Movimientos</h2>
              {historial.length === 0 ? (
                <p className="text-gray-500 italic">Aún no hay registros de movimiento.</p>
              ) : (
                <table className="w-full text-sm text-left border border-gray-300">
                  <thead className="bg-gray-200">
                    <tr>
                      <th className="px-4 py-2">Fecha</th>
                      <th className="px-4 py-2">Tipo</th>
                      <th className="px-4 py-2">Material</th>
                      <th className="px-4 py-2">Cantidad</th>
                      <th className="px-4 py-2">Responsable</th>
                      <th className="px-4 py-2">Observación</th>
                    </tr>
                  </thead>
                  <tbody>
                    {historial.map((item, i) => (
                      <tr key={i} className="border-t">
                        <td className="px-4 py-2">{item.fecha}</td>
                        <td className="px-4 py-2">{item.tipo}</td>
                        <td className="px-4 py-2">{item.nombre}</td>
                        <td className="px-4 py-2">{item.cantidad}</td>
                        <td className="px-4 py-2">{item.responsable}</td>
                        <td className="px-4 py-2">{item.observacion || item.descripcion || "-"}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
